Path: zz-rpc-core/src/main/java/com/zheng/RpcApplication.java
...
 10	
 11	/**
 12	 * RPC 框架应用
 13	 * 相当于 holder，存放了项目全局用到的变量。双检锁单例模式实现
 14	 */
 15	@Slf4j
 16	public class RpcApplication {
 17	
 18	 private static volatile RpcConfig rpcConfig;
 19	
 20	 /**
 21	 * 框架初始化，支持传入自定义配置
 22	 *
 23	 * @param newRpcConfig
 24	 */
 25	 public static void init(RpcConfig newRpcConfig) {
 26	 rpcConfig = newRpcConfig;
 27	 log.info("rpc init, config = {}", newRpcConfig.toString());
 28	 // 注册中心初始化
 29	 RegistryConfig registryConfig = rpcConfig.getRegistryConfig();
 30	 Registry registry = RegistryFactory.getInstance(registryConfig.getRegistry());
 31	 registry.init(registryConfig);
 32	 log.info("registry init, config = {}", registryConfig);
 33	
 34	 // 创建并注册 Shutdown Hook，JVM 退出时执行操作
 35	 Runtime.getRuntime().addShutdownHook(new Thread(registry::destroy));
 36	 }
