Path: zz-rpc-core/src/main/java/com/zheng/proxy/ServiceProxy.java
 1	package com.zheng.proxy;
 2	
 3	import cn.hutool.core.collection.CollUtil;
 4	import cn.hutool.http.HttpRequest;
 5	import cn.hutool.http.HttpResponse;
 6	import com.zheng.RpcApplication;
 7	import com.zheng.conf.RpcConfig;
 8	import com.zheng.constants.RpcConstant;
 9	import com.zheng.model.RpcRequest;
 10	import com.zheng.model.RpcResponse;
 11	import com.zheng.model.ServiceMetaInfo;
 12	import com.zheng.registry.Registry;
 13	import com.zheng.registry.RegistryFactory;
 14	import com.zheng.serializer.Serializer;
 15	import com.zheng.serializer.SerializerFactory;
 16	
 17	import java.io.IOException;
 18	import java.lang.reflect.InvocationHandler;
 19	import java.lang.reflect.Method;
 20	import java.util.List;
 21	
 22	/**
 23	 * 服务代理（JDK 动态代理）
 24	 *
 25	 */
 26	public class ServiceProxy implements InvocationHandler {
 27	
 28	 /**
 29	 * 调用代理
 30	 *
 31	 * @return
 32	 * @throws Throwable
 33	 */
 34	 @Override
 35	 public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {
 36	 // 指定序列化器
 37	 final Serializer serializer = SerializerFactory.getInstance(RpcApplication.getRpcConfig().getSerializer());
 38	
 39	 // 构造请求
 40	 String serviceName = method.getDeclaringClass().getName();
 41	 RpcRequest rpcRequest = RpcRequest.builder()
 42	 .serviceName(serviceName)
 43	 .methodName(method.getName())
 44	 .parameterTypes(method.getParameterTypes())
 45	 .args(args)
 46	 .build();
 47	 try {
 48	 // 序列化
 49	 byte[] bodyBytes = serializer.serialize(rpcRequest);
 50	 
 51	 // 从注册中心获取服务提供者请求地址
 52	 RpcConfig rpcConfig = RpcApplication.getRpcConfig();
 53	 Registry registry = RegistryFactory.getInstance(rpcConfig.getRegistryConfig().getRegistry());
 54	 ServiceMetaInfo serviceMetaInfo = new ServiceMetaInfo();
 55	 serviceMetaInfo.setServiceName(serviceName);
 56	 serviceMetaInfo.setServiceVersion(RpcConstant.DEFAULT_SERVICE_VERSION);
 57	 List<ServiceMetaInfo> serviceMetaInfoList = registry.serviceDiscovery(serviceMetaInfo.getServiceKey());
 58	 if (CollUtil.isEmpty(serviceMetaInfoList)) {
 59	 throw new RuntimeException("暂无服务地址");
 60	 }
 61	 ServiceMetaInfo selectedServiceMetaInfo = serviceMetaInfoList.get(0);
 62	 
 63	 // 发送请求
 64	 try (HttpResponse httpResponse = HttpRequest.post(selectedServiceMetaInfo.getServiceAddress())
 65	 .body(bodyBytes)
 66	 .execute()) {
 67	 byte[] result = httpResponse.bodyBytes();
 68	 // 反序列化
 69	 RpcResponse rpcResponse = serializer.deserialize(result, RpcResponse.class);
 70	 return rpcResponse.getData();
 71	 }
 72	 } catch (IOException e) {
 73	 e.printStackTrace();
 74	 }
 75	
 76	 return null;
 77	 }
 78	}
...
