 25	 @Override
 26	 public void handle(HttpServerRequest request) {
 27	 // 指定序列化器
 28	 final Serializer serializer = SerializerFactory.getInstance(RpcApplication.getRpcConfig().getSerializer());
 29	 System.out.println("serializer:" + serializer.getClass());
 30	
 31	 // 记录日志
 32	 System.out.println("Received request: " + request.method() + " " + request.uri());
 33	
 34	 // 异步处理 HTTP 请求
 35	 request.bodyHandler(body -> {
 36	 byte[] bytes = body.getBytes();
 37	 RpcRequest rpcRequest = null;
 38	 try {
 39	 rpcRequest = serializer.deserialize(bytes, RpcRequest.class);
 40	 } catch (Exception e) {
 41	 e.printStackTrace();
 42	 }
 43	
 44	 // 构造响应结果对象
 45	 RpcResponse rpcResponse = new RpcResponse();
 46	 // 如果请求为 null，直接返回
 47	 if (rpcRequest == null) {
 48	 rpcResponse.setMessage("rpcRequest is null");
 49	 doResponse(request, rpcResponse, serializer);
 50	 return;
 51	 }
