 9	/**
 10	 * Json 序列化器
 11	 *
 12	 */
 13	public class JsonSerializer implements Serializer {
 14	 private static final ObjectMapper OBJECT_MAPPER = new ObjectMapper();
 15	
 16	 @Override
 17	 public <T> byte[] serialize(T obj) throws IOException {
 18	 return OBJECT_MAPPER.writeValueAsBytes(obj);
 19	 }
 20	
 21	 @Override
 22	 public <T> T deserialize(byte[] bytes, Class<T> classType) throws IOException {
 23	 T obj = OBJECT_MAPPER.readValue(bytes, classType);
 24	 if (obj instanceof RpcRequest) {
 25	 return handleRequest((RpcRequest) obj, classType);
 26	 }
 27	 if (obj instanceof RpcResponse) {
 28	 return handleResponse((RpcResponse) obj, classType);
 29	 }
 30	 return obj;
 31	 }
