 21	 RpcApplication.init();
 22	
 23	 // 注册服务
 24	 String serviceName = UserService.class.getName();
 25	 LocalRegistry.register(serviceName, UserServiceImpl.class);
 26	
 27	 // 注册服务到注册中心
 28	 RpcConfig rpcConfig = RpcApplication.getRpcConfig();
 29	 RegistryConfig registryConfig = rpcConfig.getRegistryConfig();
 30	 Registry registry = RegistryFactory.getInstance(registryConfig.getRegistry());
 31	 ServiceMetaInfo serviceMetaInfo = new ServiceMetaInfo();
 32	 serviceMetaInfo.setServiceName(serviceName);
 33	 serviceMetaInfo.setServiceHost(rpcConfig.getServerHost());
 34	 serviceMetaInfo.setServicePort(rpcConfig.getServerPort());
 35	 try {
 36	 registry.register(serviceMetaInfo);
 37	 } catch (Exception e) {
 38	 throw new RuntimeException(e);
 39	 }
