Path: zz-rpc-core/src/main/java/com/zheng/registry/EtcdRegistry.java
...
 59	
 60	
 61	
 62	 @Override
 63	 public void register(ServiceMetaInfo serviceMetaInfo) throws Exception {
 64	 // 创建 Lease 和 KV 客户端
 65	 Lease leaseClient = client.getLeaseClient();
 66	
 67	 // 创建一个 30 秒的租约
 68	 long leaseId = leaseClient.grant(30).get().getID();
 69	
 70	 // 设置要存储的键值对
 71	 String registerKey = ETCD_ROOT_PATH + serviceMetaInfo.getServiceNodeKey();
 72	 ByteSequence key = ByteSequence.from(registerKey, StandardCharsets.UTF_8);
 73	 ByteSequence value = ByteSequence.from(JSONUtil.toJsonStr(serviceMetaInfo), StandardCharsets.UTF_8);
 74	
 75	 // 将键值对与租约关联起来，并设置过期时间
 76	 PutOption putOption = PutOption.builder().withLeaseId(leaseId).build();
 77	 kvClient.put(key, value, putOption).get();
 78	
 79	 // 添加节点信息到本地缓存
 80	 localRegisterNodeKeySet.add(registerKey);
 81	 }
 82	
