 31	 registry.init(registryConfig);
 32	 log.info("registry init, config = {}", registryConfig);
 33	
 34	 // 创建并注册 Shutdown Hook，JVM 退出时执行操作
 35	 Runtime.getRuntime().addShutdownHook(new Thread(registry::destroy));
 36	 }
