 107	
 108	 @Override
 109	 public List<ServiceMetaInfo> serviceDiscovery(String serviceKey) {
 110	 // 优先从缓存获取服务
 111	 List<ServiceMetaInfo> cachedServiceMetaInfoList = registryServiceCache.readCache();
 112	 if (cachedServiceMetaInfoList != null) {
 113	 return cachedServiceMetaInfoList;
 114	 }
 115	
 116	 try {
 117	 // 查询服务信息
 118	 Collection<ServiceInstance<ServiceMetaInfo>> serviceInstanceList = serviceDiscovery.queryForInstances(serviceKey);
 119	
 120	 // 解析服务信息
 121	 List<ServiceMetaInfo> serviceMetaInfoList = serviceInstanceList.stream()
 122	 .map(ServiceInstance::getPayload)
 123	 .collect(Collectors.toList());
 124	
 125	 // 写入服务缓存
 126	 registryServiceCache.writeCache(serviceMetaInfoList);
 127	 return serviceMetaInfoList;
 128	 } catch (Exception e) {
 129	 throw new RuntimeException("获取服务列表失败", e);
 130	 }
 131	 }
